import {
    USER_LOADED,
    USER_LOADING,
    AUTH_ERROR,
    LOGIN_SUCCESS,
    LOGIN_FAIL,
    LOADING,
    LOGOUT_SUCCESS,
    REGISTER_SUCCESS,
    REGISTER_FAIL
} from '../actions/types';

const initialState = {
    token: localStorage.getItem('token'),
    isAuthenticated: null,
    isLoading: false,
    authLoading: false,
    user: null
};

export default function (state = initialState, action) {
    switch (action.type) {
        case LOADING: return {
            ...state, authLoading: true
        }
        case USER_LOADING: return {
            ...state, isLoading: true
        }
        case USER_LOADED: return {
            ...state,
            isAuthenticated: true,
            isLoading: false,
            user: action.payload
        }
        case LOGIN_SUCCESS:
        case REGISTER_SUCCESS:
            localStorage.setItem('token', action.payload.token);
            return {
                ...state,
                ...action.payload,
                isAuthenticated: true, authLoading: false,
            }
        case AUTH_ERROR:
        case LOGIN_FAIL:
        case LOGOUT_SUCCESS:
        case REGISTER_FAIL:
            localStorage.removeItem('token');
            return {
                ...state,
                token: null,
                user: null,
                isAuthenticated: false,
                authLoading: false,
            }
        default: return state;
    }
}
